mapper.select:
SELECT * FROM hotels WHERE id IN (
    SELECT id FROM (
        SELECT
            id,
            POWER((longitude-#{longitude}),2)+POWER((latitude-#{latitude}),2) AS dis
        FROM hotels
    ) Q1 ORDER BY dis LIMIT #{RETURN_CNT}
)

SELECT country_id FROM cities WHERE id = (
    SELECT city_id FROM hotels WHERE id = 1
)
